package main

import (
    "fmt"
    "log"
    "net/http"
    "github.com/gorilla/mux" // Install using: go get -u github.com/gorilla/mux
)

type User struct {
    ID   string
    Name string}

var users = []User{
    {ID: "1", Name: "John Doe"},
    {ID: "2", Name: "Jane Doe"},
    {ID: "3", Name: "Bob Smith"},}

func getUser(w http.ResponseWriter, r *http.Request) {
    vars := mux.Vars(r)
    idStr := vars["id"]

    for _, user := range users {
        if user.ID == idStr {
            fmt.Fprintf(w, "ID: %s, Name: %s\n", user.ID, user.Name)
            break
        }
    }}

func main() {
    router := mux.NewRouter()
    router.HandleFunc("/api/users/{id}", getUser).Methods("GET")

    log.Fatal(http.ListenAndServe(":8080", router))}

