function bubbleSort(arr) {
    let swap;
    let n = arr.length;

    do {
        swap = false;
        for (let i = 0; i < n - 1; i++) {
            if (arr[i] > arr[i + 1]) {
                const temp = arr[i];
                arr[i] = arr[i + 1];
                arr[i + 1] = temp;
                swap = true;
            }
        }
        n--;
    } while (swap);

    return arr;}

const sortedArr = bubbleSort([5, 2, 9, 1, 5, 6]);
