std::vector<int> merge_sorted_vectors(std::vector<int>& a, std::vector<int>& b) {
    std::vector<int> merged;
    int i = 0, j = 0;

    while (i < a.size() && j < b.size()) {
        if(a[i] <= b[j]) {
            merged.push_back(a[i]);
            i++;
        } else {
            merged.push_back(b[j]);
            j++;
        }
    }

    while (i < a.size()) {
        merged.push_back(a[i]);
        i++;
    }

    while (j < b.size()) {
        merged.push_back(b[j]);
        j++;
    }

    return merged;}

int main() {
    std::vector<int> a = {1, 3, 5};
    std::vector<int> b = {2, 4, 6};
    std::vector<int> result = merge_sorted_vectors(a, b);

    for (const int& num : result) {
        std::cout << num << ' ';
    }

    return 0;}

