function convertTo24Hour(time) {
  const [hours, minutes, secondsWithSuffix] = time.split(':');
  const [seconds, suffix] = [secondsWithSuffix.slice(0, 2), secondsWithSuffix.slice(2)];

  let newHours = parseInt(hours);
  if (suffix === 'AM' && newHours === 12) newHours = 0;
  if (suffix === 'PM' && newHours !== 12) newHours += 12;

  return `${newHours.toString().padStart(2, '0')}:${minutes}:${seconds}`;
}

