function caesarCipher(str, offset) {
    return str.replace(/[a-zA-Z]/g, function(c) {
        const charCode = c.charCodeAt(0);
        const base = charCode >= 97 && charCode <= 122 ? 97 : 65;
        return String.fromCharCode(((charCode - base + offset) % 26) + base);
    });
}

