int count_words(const char *filename) {
    FILE *file = fopen(filename, 'r');
    if (!file) {
        printf('Error opening file.\n');
        return -1;
    }

    int word_count = 0;
    bool in_word = false;

    int c;
    while ((c = fgetc(file)) != EOF) {
        if (isspace(c)) {
            in_word = false;
        } else if (!in_word) {
            in_word = true;
            word_count++;
        }
    }

    fclose(file);
    return word_count;}

