std::vector<std::vector<int>> transpose_matrix(const std::vector<std::vector<int>>& matrix) {
    size_t rows = matrix.size();
    size_t cols = matrix[0].size();
    std::vector<std::vector<int>> transposed_matrix(cols, std::vector<int>(rows));

    for (size_t row = 0; row < rows; ++row) {
        for (size_t col = 0; col < cols; ++col) {
            transposed_matrix[col][row] = matrix[row][col];
        }
    }

    return transposed_matrix;}

int main() {
    std::vector<std::vector<int>> matrix = {{1, 2, 3}, {4, 5, 6}, {7, 8, 9}};
    std::vector<std::vector<int>> transposed = transpose_matrix(matrix);

    // Output the transposed matrix
    for (const auto& row: transposed) {
        for (const int value : row) {
            std::cout << value << ' ';
        }
        std::cout << std::endl;
    }

    return 0;}

