int count_word_occurrences(const std::string& file_path, const std::string& word) {
    std::ifstream file(file_path);
    std::string line;
    int count = 0;

    while (std::getline(file, line)) {
        std::size_t pos = 0;
        while ((pos = line.find(word, pos)) != std::string::npos) {
            count++;
            pos += word.length();
        }
    }

    return count;}

