#include <vector>
#include <deque>
#include <iostream>

std::vector<int> sliding_window_maximum(const std::vector<int>& nums, int window_size) {
    std::deque<int> dq;
    std::vector<int> result;

    for (size_t i = 0; i < nums.size(); ++i) {
        if (!dq.empty() && dq.front() == i - window_size) {
            dq.pop_front();
        }

        while (!dq.empty() && nums[dq.back()] < nums[i]) {
            dq.pop_back();
        }

        dq.push_back(i);

        if (i >= window_size - 1) {
            result.push_back(nums[dq.front()]);
        }
    }
    return result;}

int main() {
    std::vector<int> nums = {1, 3, -1, -3, 5, 3, 6, 7};
    int window_size = 3;

    std::vector<int> result = sliding_window_maximum(nums, window_size);

    for (const auto& num : result) {
        std::cout << num << ' ';
    }}

