function longestConsecutiveSubsequence(arr) {
    const set = new Set(arr);
    let longest = 0;

    for (let num of set) {
        if (!set.has(num - 1)) {
            let curr = num;
            let count = 1;

            while (set.has(curr + 1)) {
                curr += 1;
                count += 1;
            }

            longest = Math.max(longest, count);
        }
    }
    return longest;}

