    int left = 0;
    int right = inputChars.length - 1;
    while (left < right) {
        char temp = inputChars[left];
        inputChars[left] = inputChars[right];
        inputChars[right] = temp;
        left++;
        right--;
    }
    return new String(inputChars);
}

