import os
import time
from watchdog.observers import Observer
from watchdog.events import FileSystemEventHandler

class DirectoryUpdateHandler(FileSystemEventHandler):
    def on_modified(self, event):
        if event.src_path.endswith('.log'):
            return
        with open('monitor.log', 'a') as log_file:
            log_file.write(f'{event.src_path} was updated at {time.ctime()}\n')

def monitor_directory(path):
    event_handler = DirectoryUpdateHandler()
    observer = Observer()
    observer.schedule(event_handler, path, recursive=True)
    observer.start()

    try:
        while True:
            time.sleep(1)
    except KeyboardInterrupt:
        observer.stop()
    observer.join()


if __name__ == '__main__':
    dir_path = '/path/to/watch/directory'
    monitor_directory(dir_path)

