function isBalanced(s) {
    const brackets = {
        '(': ')',
        '[': ']',
        '{': '}'
    };
    const stack = [];

    for (const char of s) {
        if (brackets[char]) {
            stack.push(char);
        } else {
            const last = stack.pop();
            if (brackets[last] !== char) {
                return false;
            }
        }
    }

    return stack.length === 0;}

