{   
    static Dictionary<int, int> cache = new Dictionary<int, int>();

    public static int MemoizedFibonacci(int n)
    {
        if (n <= 1) return n;
        if (!cache.ContainsKey(n))
        {
            cache[n] = MemoizedFibonacci(n - 1) + MemoizedFibonacci(n - 2);
        }
        return cache[n];
    }

