public class LongestCommonPrefix {
    public static void main(String[] args) {
        List<String> words = Arrays.asList("flower", "flow", "flight");
        System.out.println(findLongestCommonPrefix(words));
    }

    public static String findLongestCommonPrefix(List<String> words) {
        if(words == null || words.size() == 0) {
            return "";
        }
        String prefix = words.get(0);
        for(int i = 1; i < words.size(); i++) {
            while(!words.get(i).startsWith(prefix)) {
                prefix = prefix.substring(0, prefix.length() - 1);
                if(prefix.isEmpty()) {
                    return "";
                }
            }
        }
