void selection_sort(std::vector<int>& vec) {
    for (size_t i = 0; i < vec.size() - 1; ++i) {
        size_t min_index = i;
        for (size_t j = i + 1; j < vec.size(); ++j) {
            if (vec[j] < vec[min_index]) {
                min_index = j;
            }
        }
        std::swap(vec[i], vec[min_index]);
    }}

