#include <vector>
#include <algorithm>

std::string find_longest_common_prefix(const std::vector<std::string>& words) {
    if (words.empty()) return "";
    std::string prefix = words[0];
    for (const std::string& word : words) {
        while (word.find(prefix) != 0) {
            prefix = prefix.substr(0, prefix.length() - 1);
            if (prefix.empty()) return "";
        }
    }
    return prefix;}

int main() {
    std::vector<std::string> words = {"string", "struggle", "street", "straw"};
    std::string longest_prefix = find_longest_common_prefix(words);
    std::cout << longest_prefix << std::endl;
    return 0;}

