function splitStringIntoWords(str) {
    let words = [];
    let currentWord = "";
    for (const char of str) {
        if (char === ' ') {
            if (currentWord.length) {
                words.push(currentWord);
                currentWord = "";
            }
        } else {
            currentWord += char;
        }
    }
    if (currentWord.length) {
        words.push(currentWord);
    }
    return words;
}

