    ListNode* next;
    ListNode(int x) : val(x), next(NULL) {}
};

bool hasCycle(ListNode* head) {
    if (head == NULL || head->next == NULL) {
        return false;
    }
    ListNode* slow = head;
    ListNode* fast = head->next;
    while (fast != slow) {
        if (fast == NULL || fast->next == NULL) {
            return false;
        }
        slow = slow->next;
        fast = fast->next->next;
    }
    return true;
}

