bool areBalanced(std::string str) {
  std::stack<char> parentheses;

  for (char c : str) {
    if (c == '(' || c == '{' || c == '[') {
      parentheses.push(c);
    } else {
      if (parentheses.empty()) return false;

      char top = parentheses.top();
      parentheses.pop();

      if ((c == ')' && top != '(') ||
          (c == '}' && top != '{') ||
          (c == ']' && top != '[')) {
        return false;
      }
    }
  }

  return parentheses.empty();
}

