function calculateCompoundInterest(initialAmount, annualInterestRate, timesCompoundedPerYear, years) {
  let factor = 1 + (annualInterestRate / 100 / timesCompoundedPerYear);
  let power = timesCompoundedPerYear * years;
  let compoundInterest = initialAmount * Math.pow(factor, power);
  return compoundInterest;
}

