public static List<int> GetFactors(int number) {
    List<int> factors = new List<int>();

    for (int i = 1; i <= Math.Sqrt(number); i++) {
        if (number % i == 0) {
            factors.Add(i);

            // If the number is not a perfect square, add the other factor as well
            if (i != number / i) {
                factors.Add(number / i);
            }
        }
