float euclideanDistance(const Point3D &p1, const Point3D &p2) {
    float deltaX = p1.x - p2.x;
    float deltaY = p1.y - p2.y;
    float deltaZ = p1.z - p2.z;

    return std::sqrt(deltaX * deltaX + deltaY * deltaY + deltaZ * deltaZ);
}

