bool is_balanced(const std::string& sentence) {
    std::stack<char> parentheses;

    for (char ch : sentence) {
        if (ch == '(') {
            parentheses.push(ch);
        } else if (ch == ')') {
            if (parentheses.empty()) return false;
            parentheses.pop();
        }
    }

    return parentheses.empty();
}

