public class Fibonacci {

    public static ArrayList<Integer> fibonacci(int n) {
        ArrayList<Integer> fibs = new ArrayList<>()
        return fibonacciRecursion(n, fibs);
    }

    private static ArrayList<Integer> fibonacciRecursion(int n, ArrayList<Integer> fibs) {
        if (n == 0) {
            return fibs;
        } else {
            int size = fibs.size();
            int nextFib = (size <= 1) ? size : fibs.get(size - 1) + fibs.get(size - 2);
            fibs.add(nextFib);
            return fibonacciRecursion(n - 1, fibs);
        }
    }
}

