    let len = arr.length;
    let swapped;
    do {
        swapped = false;
        for (let i = 1; i < len; i++) {
            if (arr[i - 1] > arr[i]) {
                [arr[i - 1], arr[i]] = [arr[i], arr[i - 1]];
