double medianOfVector(const std::vector<int> &numbers) {
    std::vector<int> numbersCopy(numbers);
    std::sort(numbersCopy.begin(), numbersCopy.end());
    size_t size = numbersCopy.size();
    double median = 0.0;
    if (size % 2 == 0) {
        median = (numbersCopy[size / 2 - 1] + numbersCopy[size / 2]) / 2.0;
