def sieve_of_eratosthenes(limit):
    primes = [True] * (limit + 1)
    primes[0], primes[1] = False, False
    n = 2
    while n * n <= limit:
        if primes[n]:
            for i in range(n * n, limit + 1, n):
                primes[i] = False
        n += 1
    return [x for x in range(2, limit + 1) if primes[x]]

